import React,{useState} from "react";


function App(){
const [score1,setScore1]=useState(0);
const [score2,setScore2]=useState(0);

let winningScore=3;
const on1st=()=>{
    setScore1(score1 + 1)
}
const on2nd=()=>{
    setScore2(score2 + 1)
}

    return (
    <div>
      <h1>{score1}t0 {score2}</h1>
      <button onClick={on1st}>p1</button> 
      <button onClick={on2nd}>p2</button>
        </div>
    )
};
export default App;
