import React,{useState} from "react";


function App(){
const [score1,setScore1]=useState(0);
const [score2,setScore2]=useState(0);

    return (
    <div>
      <h1>{score1}t0 {score2}</h1>
      <button onClick={()=>{setScore1(score1 + 1)}}>p1</button> 
      <button onClick={()=>{setScore2(score2 + 1)}}>p2</button>
        </div>
    )
};
export default App;
