import { findAllByDisplayValue } from "@testing-library/react";
import React,{useState} from "react";


function App(){
const [score1,setScore1]=useState(0);
const [score2,setScore2]=useState(0);
const [winningScore,setWinningScore]=useState(5);

const [gameState,setGameState]=useState(false);


const onValueChange=(e)=>{
    // 
    console.log(e.target.value)
    }
     
const on1st=()=>{
    setScore1(score1 + 1)
    if(score1===winningScore){
        setGameState(true)
        console.log("gameover")
    }
}
const on2nd=()=>{
    setScore2(score2 + 1)
    if(score2===winningScore){
        setGameState(true)
        console.log("gameover")
    }
}


    return (
    <div>
      <h1>{score1}t0 {score2}</h1>
      <select name="" id="range" value={winningScore} onChange={onValueChange}> 
    <option value="3">3</option>
    <option value="4">4</option>
    <option value="5">5</option>
    <option value="6">6</option>
    <option value="7">7</option>
    <option value="8">8</option>
    <option value="9">9</option>
    <option value="10">10</option>
    </select>
      <button onClick={on1st} disabled={gameState}>p1</button> 
      <button onClick={on2nd} disabled={gameState}>p2</button>
        </div>
    )
};
export default App;
